document.addEventListener("DOMContentLoaded", function () {
    // Seleccionar el botn
    const botonPrestamo = document.querySelector('.sharing-club-button'); // Cambia 'sharing-club-button' por la clase especfica del botn si es diferente.

    // Verificar si el botn existe
    if (botonPrestamo) {
        botonPrestamo.addEventListener("click", function (e) {
            e.preventDefault(); // Evitar la accin predeterminada si es necesario

            // Mostrar el mensaje
            const mensaje = document.createElement("div");
            mensaje.style.marginTop = "20px";
            mensaje.style.fontSize = "18px";
            mensaje.style.color = "#4CAF50";
            mensaje.style.fontWeight = "bold";
            mensaje.style.border = "2px solid #4CAF50";
            mensaje.style.padding = "10px";
            mensaje.style.borderRadius = "8px";
            mensaje.textContent =
                " Prstamo realizado con xito! Acrcate a la biblioteca para recoger tu ejemplar. ";

            // Agregar el mensaje al DOM
            botonPrestamo.parentNode.appendChild(mensaje);
        });
    }
});

